StylingConfiguration:
{"customStyles":".outer-header{
   box-sizing: border-box;
    display: flex;
    flex-direction: row;
    align-items: flex-start;
    padding: 32px, 48px;
    gap: 32px;
    width: 100%;
    height: 156px;
    background: #261040;
    border-bottom: 1px solid #EEEEEE;
    scroll-behavior: normal;
    position: sticky;
    top: 0;

    flex: none;
    order: 1;
    flex-grow: 0;
}

.inner-header{
     box-sizing: border-box;
    display: flex;
    flex-direction: row;
    align-items: flex-start;
    padding: 32px, 48px;
    gap: 32px;
  
    height: 82px;
    width: 100%;
    background: #261040;
    margin-top: 0;
    position: sticky;
    top: 0;
    
    flex: none;
    order: 1;
    flex-grow: 0;
}

.save-btn{
    display: flex;
flex-direction: row;
justify-content: center;
align-items: flex-start;
padding: 14px 24px;
gap: 10px;

width: 170px;
height: 40px;
position: absolute;
right: 9.5%;
top: 20%;

background: #51376B;
border-radius: 2px;

flex: none;
order: 2;
flex-grow: 0;
}

.save-text{
 
font-family: 'Roboto';
font-style: normal;
font-weight: 500;
font-size: 15px;
line-height: 18px;

display: flex;
align-items: center;
text-align: left;
margin-left: 5px;

color: #FFFFFF;

flex: none;
order: 0;
flex-grow: 0;

}
.save-text :hover{
    color: #cccccc;
}

.first-text{
   width: 224px;
height: 16px;
font-family: 'Roboto';
font-style: normal;
font-weight: 500;
font-size: 14px;
line-height: 16px;
letter-spacing: 0.25px;
vertical-align: top;
color: #000000;
position: absolute;
left: 3%;
right: 3%;

flex: none;
order: 0;
flex-grow: 0;
}

.first-box{
    display: flex;
flex-direction: row;
align-items: center;
padding: 2px 8px;
gap: 10px;

width: 240px;
height: 20px;
position: absolute;
top: 20%;

background: #F8F6FB;
border-radius: 2px;

flex: none;
order: 0;
flex-grow: 0;
}

.title{
   width: 1325px;
height: 34px;
font-family: 'Roboto';
font-style: normal;
font-weight: 500;
font-size: 30px;
line-height: 34px;
vertical-align: baseline;
display: flex;
align-items: flex-end;
color: #FFFFFF;
position: absolute;
top: 60%;
left: 3%;
right: 3%;
flex: none;
order: 0;
flex-grow: 0;
}

.sub-title{
    width: 1325px;
height: 22px;

font-family: 'Roboto';
font-style: normal;
font-weight: 400;
font-size: 15px;
line-height: 22px;

color: #FFFFFF;
position: absolute;
top: 90%;
left: 3%;
right: 3%;

flex: none;
order: 1;
flex-grow: 0;
}

.draft{
    display: flex;
flex-direction: row;
justify-content: center;
align-items: center;
 height: 40px;
 width: 42px;
 padding: 0px;
 position: absolute;

left: 91%;
top: 20%;

background: #51376B;
border-radius: 2px;


flex: none;
order: 2;
flex-grow: 0;
